import React, { useEffect, useState } from 'react'
import * as Components from '../Components/index'
import './Styles/Aboutus.css'
import person from '../Assets/person.png'

function Aboutus() {
  const dummydata = {
    Team :
    [{
      name:'Jhon Wick',
      post:'Creative Director',
      img: {person}
    },{
      name:'Jhon Wick',
      post:'Creative Director',
      img: {person}
    },{
      name:'Jhon Wick',
      post:'Creative Director',
      img: {person}
    },{
      name:'Jhon Wick',
      post:'Creative Director',
      img: {person}
    },{
      name:'Jhon Wick',
      post:'Creative Director',
      img: {person}
    },{
      name:'Jhon Wick',
      post:'Creative Director',
      img: {person}
    },{
      name:'Jhon Wick',
      post:'Creative Director',
      img: {person}
    },{
      name:'Jhon Wick',
      post:'Creative Director',
      img: {person}
    },]
  }
  let y = dummydata.Team.length
  let x
  const [blockleft,setblockleft] = useState(true)
  const [blockright,setblockright] = useState(false)
  const [left,setleft] = useState(0)

  function moveright(){
    x = left-400
      console.log(x,y)
      setleft(x)
  }
  function moveleft(){
    x = left+400
      console.log(x)
    if (x>0){
      setleft(0)
      setblockleft(true)
    }
    else{
      setleft(x)
      setblockright(false)
    }
}

  return (
    <div className='center'>
      <Components.Aboutus/>
      <div className='teambox'>
        <div className='teamheading'>Team</div>
        <div className='teamgroup'>
          <div className='leftarrow' onClick={moveleft} disabled={blockleft}>
            <left/>
          </div>
          <div className='teamgroupinner'>
            <div className='insideteam' style={{transform: `translateX(${left}px)`}}>
              <div className='teammeb on'></div>
              <div className='teammeb tw'></div>
              <div className='teammeb thre'></div>
              <div className='teammeb fou'></div>
              <div className='teammeb fiv'></div>
              <div className='teammeb si'></div>
              <div className='teammeb thre'></div>
              <div className='teammeb fou'></div>
            </div>
          </div>
          <div className='rightarrow' onClick={moveright} disabled={blockright}></div>
        </div>
      </div>
    </div>
  )
}

export default Aboutus
